import React from 'react';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const AnalyticsChart = ({ title, data, labels, color = 'blue' }) => {
  const chartData = {
    labels,
    datasets: [
      {
        label: title,
        data,
        borderColor: `rgb(${
          color === 'blue' ? '59, 130, 246' :
          color === 'green' ? '34, 197, 94' :
          color === 'red' ? '239, 68, 68' :
          '156, 163, 175'
        })`,
        backgroundColor: `rgba(${
          color === 'blue' ? '59, 130, 246' :
          color === 'green' ? '34, 197, 94' :
          color === 'red' ? '239, 68, 68' :
          '156, 163, 175'
        }, 0.1)`,
        tension: 0.4,
        fill: true
      }
    ]
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: title
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        ticks: {
          callback: function(value) {
            return value + ' TRX';
          }
        }
      }
    }
  };

  return (
    <div className="bg-white p-4 rounded-lg shadow-md">
      <Line data={chartData} options={options} />
    </div>
  );
};

export default AnalyticsChart;
